#ifndef CONFIG_WIFI_HTML_H
#define CONFIG_WIFI_HTML_H
/**
 * @brief 配网HTML代码 本段代码改编自 https://blog.csdn.net/weixin_44220583/article/details/111562423
 */
const char *page_html = "<!doctype html><html lang='en'><head><meta charset='utf-8'><meta name='viewport'content='width=device-width,initial-scale=1,maximum-scale=1,minimum-scale=1,user-scalable=no'><title>飞鱼梦工坊wifi配网程序</title><style>*{margin:0;padding:0}body{font-size:16px;margin:0 15px;z-index:0}.flexcenter{display:flex;align-content:center;align-items:center}.button{width:40%;border-radius:10px;height:40px;background:#0283ef;border:1px solid#0283ef;font-size:20px;justify-content:space-evenly;color:#fff;font-weight:100}.button:active{background:#0283cf}.button-re{width:40%;border-radius:10px;height:40px;background:#ff5f4e;border:1px solid#ff5f4e;font-size:20px;justify-content:space-evenly;color:#fff;font-weight:100}.button-re:active{background:#df5f4e}.wifititle{display:flex;margin:0 15px;justify-content:space-between;font-size:15px;color:#ccc;font-weight:300}.wifilist{display:flex;flex-direction:column;align-items:center;font-size:18px;font-weight:100;overflow:auto}wifi{height:40px;border-bottom:1px solid#e2e2e2;width:90%;display:flex;align-items:flex-end}wifi:active{background:#50bfff}.title{justify-content:center;height:10%;font-size:35px;color:#252a6c;letter-spacing:5px;text-shadow:0 12px 10px#2f3049}.content-input{height:20%;max-height:20%;display:flex;flex-direction:column}.input-text{display:flex;flex-direction:row;align-items:flex-end;justify-content:space-evenly;font-size:20px;color:#ccc;font-weight:700}.input{width:76%;line-height:30px;font-size:18px;margin-top:15px;border-top:0;border-left:0;border-right:0;border-bottom:1px solid#e2e2e2;font-weight:300}.context-button{margin-top:15px;margin-bottom:15px;justify-content:space-around}.loading-svg-container{width:50px;height:50px}.path{stroke:#409eff;stroke-width:2;stroke-dasharray:95,126;stroke-dashoffset:0;animation:loading-dash 1.5s ease-in-out infinite}@keyframes loading-dash{0%{stroke-dasharray:1,126;stroke-dashoffset:0}50%{stroke-dasharray:95,126;stroke-dashoffset:-31px}to{stroke-dasharray:6,120;stroke-dashoffset:-120px}}.loading{width:40%;height:15%;position:absolute;background:#fff;display:flex;align-items:center;justify-content:space-around;top:30%;left:30%;flex-direction:column;color:#f4606c;border-radius:20px;overflow:hidden;font-size:14px;z-index:999}.box{width:100%;height:100%;position:fixed;background:#ccc;opacity:.8;margin-left:-15px;display:none}</style></head><body onload=initData()><div class='box'><div class='loading'><svg viewBox='0 0 50 50'class='loading-svg-container'><circle cx='25'cy='25'r='20'fill='none'class='path'></circle></svg><span id='tip'></span></div></div><div class='flexcenter title'>飞鱼梦工坊配网</div><div class='content-input'><div class='input-text'>SSID<input class='input'id='ssid'placeholder='选择WiFi或手动输入WiFi'></div><div class='input-text'>密码<input class='input'id='pwd'placeholder='输入WiFi密码'type='password'></div></div><div class='flexcenter context-button'><button class='flexcenter button'onclick=connwifi()>提&nbsp;&nbsp;交</button><button class='flexcenter button-re'onclick=reset()>重&nbsp;&nbsp;置</button></div><div class='wifititle'><span>wifi列表</span><span style='color:#0283ef'onclick=getWifiList()>刷新</span></div><div class='wifilist'id='wifilist'></div></body><script>function initData(){setTimeout(function(){getWifiList()},1e3)}function showloading(e){document.getElementsByClassName('box')[0].style.display='table',document.getElementById('tip').innerHTML=e}function hideloading(){document.getElementsByClassName('box')[0].style.display='none',document.getElementById('tip').innerHTML=''}function getWifiList(){document.getElementById('tip').innerText='',showloading('搜索wifi中...'),doget('/wifilist',function(e){if(e.indexOf('.nodata')!==-1){showloading('找不到有效wifi信息'),setTimeout(function(){hideloading()},2e3);return}hideloading();var n=e.split(','),t='',s=window.innerHeight,o=document.getElementsByClassName('wifititle')[0].offsetTop;document.getElementById('wifilist').style.height=s-o-20+'px';for(item of n){if(item==='')continue;t+='<wifi onclick=\"setSsid(event)\">'+item+'</wifi>'}document.getElementById('wifilist').innerHTML=t})}function setSsid(e){document.getElementById('tip').innerText='',document.getElementById('ssid').value=e.currentTarget.innerText}function reset(){document.getElementById('tip').innerText='',document.getElementById('ssid').value='',document.getElementById('pwd').value=''}function connwifi(){var t,n,e=document.getElementById('ssid').value;if(e===''||e===null||typeof e=='undefined'){showloading('SSID不能为空'),setTimeout(function(){hideloading()},1e3);return}t=document.getElementById('pwd').value,n='/configwifi?ssid='+e+'&pwd='+t,showloading('网络配置中...'),doget(n,function(e){showloading(e),setTimeout(function(){hideloading()},1e3)})}function doget(e,t){var n=new XMLHttpRequest;n.open('get',e),n.send(),n.onreadystatechange=function(){t(n.response)}}</script></html>";
#endif